import { useEffect, useState } from 'react';
import 'slider.css';

const Slider = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const moveSlider = () => {
            setCurrentSlide((prevSlide) => (prevSlide + 1) % 7); // 7 slides total
        };
        const interval = setInterval(moveSlider, 2000); // 2 seconds interval
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="slider-outer-container">
            <div className={`slider-container slide-${currentSlide}`}>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/kithremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/mobilesremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/earphonesremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/lapremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/lapremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/lapremovebgpreview-1@2x.png" />
                </div>
                <div className="slider-item">
                    <img className="slider-image" alt="" src="/lapremovebgpreview-1@2x.png" />
                </div>
            </div>
        </div>
    );
};

export default Slider;
