import React, { useState } from 'react';

function SideNavbar({ onSelect }) {
  const [activeIndex, setActiveIndex] = useState(0);

  const menuItems = [
    'Home, Kitchen',
    'Electronics',
    'Mobiles, Computers',
    'TV, Appliances',
    'Sports, Fitness'
  ];

  return (
    <div className='fixed top-0 left-0 h-full w-64 bg-whitesmoke-100 transform -translate-x-full transition-transform duration-300 ease-in-out' id='sidebar'>
      <div className='py-8'>
        <ul className="list-none p-0 m-0">
          {menuItems.map((item, index) => (
            <li
              key={index}
              className="relative px-4 py-8 cursor-pointer"
              onClick={() => setActiveIndex(index)}
            >
              {activeIndex === index && (
                <div className='grid grid-cols-12 absolute inset-0 bg-silver z-0'>
                  <div className='grid col-span-1 bg-darkslategray-100' style={{ zIndex: 1 }}></div>
                </div>)}
              <a className="px-8 relative z-10 text-11xl">{item}</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default SideNavbar;
