        import React, { useEffect, useState } from 'react';

const Slider = () => {
const slides = [

    { imgUrl: "/kithremovebgpreview-1@2x.png", alt: "Slide 1" },
    { imgUrl: "/lapremovebgpreview-1@2x.png", alt: "Slide 2" },
    { imgUrl: "/earphonesremovebgpreview-1@2x.png", alt: "Slide 3" },
    { imgUrl: "/mobilesremovebgpreview-1@2x.png", alt: "Slide 4" }
];

const slideCount = slides.length;

const [currentSlide, setCurrentSlide] = useState(0);

const sliderContainers = document.querySelectorAll('.slider-inner-container');


const moveSlider = () => {
    const newSlide = (currentSlide + 1) % slideCount;

    sliderContainers[currentSlide].style.transform = 'translateX(-100%)';
    sliderContainers[newSlide].style.transform = 'translateX(0)';

    setCurrentSlide(newSlide);
};

useEffect(() => {
    const interval = setInterval(moveSlider, 3000); // Adjust the interval time as needed
    return () => clearInterval(interval);
}, []);

return (
    <div className="slider-outer-container">
    <div className="absolute top-[0px] left-[0px] bg-whitesmoke-100 w-[1046px] h-[196px]" />
    <div className="slider-inner-container">
        {slides.map((slide, index) => (
        <div key={index} className={`slider-container slider-${index}`}>
            <div className="slider-item">
            <div className={`absolute top-[18px] left-[725.67px] rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px] ${currentSlide === index ? 'active' : ''}`}>
                <img
                className="w-[169.06px] h-[167px] object-cover"
                alt={slide.alt}
                src={slide.imgUrl}
                />
            </div>
            </div>
        </div>
        ))}
    </div>
    </div>
);
};

export default Slider;
