import React, { useState, useEffect } from 'react';

const Slider = () => {
const [currentSlide, setCurrentSlide] = useState(0);

const slides = [
    {
    src: '/kithremovebgpreview-1@2x.png',
    alt: 'Kith Product',
    },
    {
    src: '/mobilesremovebgpreview-1@2x.png',
    alt: 'Mobiles Product',
    },
    {
    src: '/earphonesremovebgpreview-1@2x.png',
    alt: 'Earphones Product',
    },
    {
    src: '/lapremovebgpreview-1@2x.png',
    alt: 'Laptop Product',
    },
];

useEffect(() => {
    const interval = setInterval(() => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 3000);

    return () => clearInterval(interval);
}, []);

return (
    <div className="slider-outer-container">
    <div className="absolute top-[0px] left-[0px] bg-whitesmoke-100 w-[1046px] h-[196px]" />
    <div className="slider-inner-container">
        <div className="slider-track">
        {slides.map((slide, index) => (
            <div
            key={index}
            className="slider-container"
            style={{ transform: `translateX(${(index - currentSlide) * 100}%)` }}
            >
            <div className="slider-item">
                <div className="absolute top-[18px] left-[50%] rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px]">
                <img
                    className="w-[169.06px] h-[167px] object-cover"
                    alt={slide.alt}
                    src={slide.src}
                />
                </div>
            </div>
            </div>
        ))}
        </div>
    </div>
    </div>
);
};

export default Slider;
