import React from "react";

type Product = {
id: number;
name: string;
imageSrc: string;
store: string;
price: number;
d_price: number;
discount: number;
description: string;
link: string;
};

type ProductCardProps = {
product: Product;
onProductClick: (product: Product) => void;
onAddToCartClick: (product: Product) => void;
};

const ProductCard: React.FC<ProductCardProps> = ({
product,
onProductClick,
onAddToCartClick,
}) => {
return (
    <div className="absolute w-full top-[0px] flex flex-wrap -mx-2">
    <div className="absolute top-[743px] left-[362px] w-[223px] h-[265px]">
    <div className="absolute top-[0px] left-[0px] bg-whitesmoke-100 w-[223px] h-[265px]" />
        <img
        className="absolute top-[216px] left-[13px] w-[34px] h-px"
        alt=""
        src="/line-11.svg"
        />
        <div className="absolute top-[0px] left-[0px] bg-white shadow-[1px_1px_2px_rgba(0,_0,_0,_0.25)_inset] w-[221px] h-[143px]" />
            <img
            className="absolute top-[5px] left-[48px] w-[127px] h-[133px] object-cover cursor-pointer"
            src={product.imageSrc}
            alt={product.name}
            onClick={() => onProductClick(product)}
            />

        <img
        className="absolute h-[7.55%] w-[8.97%] top-[2.64%] right-[3.59%] bottom-[89.81%] left-[87.44%] max-w-full overflow-hidden max-h-full"
        alt=""
        src="/vector9.svg"
        />
        <a
        className="absolute top-[158px] left-[10px] text-[inherit] inline-block w-[199px] h-[39px] [text-decoration:none]"
        href="https://roobai.com/deal/boat-smartwatches-starts-899-bo8r0"
        target="_blank"
        >
        {product.name}
        </a>
        <div className="absolute h-[8.68%] w-[19.28%] top-[78.11%] left-[42.6%] text-red inline-block">
        {product.discount}%
        </div>
        <img
        className="absolute h-[4.72%] w-[1.12%] top-[61.13%] right-[10.09%] bottom-[34.15%] left-[88.79%] max-w-full overflow-hidden max-h-full"
        alt=""
        src="/vector10.svg"
        />
        <div className="absolute top-[207px] left-[139px] text-black inline-block w-[72px] h-[23px]">
        â‚¹{product.d_price}
        </div>
        <div className="absolute top-[229.5px] left-[0.5px] box-border w-[223px] h-px border-t-[1px] border-solid border-silver" />
        <img
        className="absolute h-[8.49%] w-[11.21%] top-[88.3%] right-[63.68%] bottom-[3.21%] left-[25.11%] max-w-full overflow-hidden max-h-full"
        alt=""
        src="/vector11.svg"
        />
        <div className="absolute top-[229.5px] left-[106.5px] box-border w-px h-9 border-r-[1px] border-solid border-silver" />
        <a href="https://www.amazon.in/dp/B07XCM6T4N/ref=cm_sw_r_as_gl_apa_gl_i_QCNG9S25E2BEASAKR92W?linkCode=ml2&tag=mystore1060a-21 "
            style={{
            textDecoration: "none",
            color: "inherit",
            cursor: "pointer",
            }}>
        <div className="absolute top-[237px] left-[10px] inline-block w-[58px] h-6">{`Shop `}</div>
        </a>
        <div className="absolute top-[237px] left-[114px] inline-block w-[109px] h-6">
            <button className="relative text-left text-base text-darkslategray-100 font-sora"

            onClick={() => onAddToCartClick(product)}>
            Add to cart
            </button>
        </div>
        <div className="absolute top-[207px] left-[10px] text-silver inline-block w-16 h-[23px]">
        â‚¹{product.price}
        </div>
        <div className="absolute top-[123px] left-[10px] font-semibold inline-block w-[69px] h-2.5">
        Amazon
        </div>
        <img
        className="absolute h-[4.91%] w-[7.62%] top-[79.62%] right-[57.85%] bottom-[15.47%] left-[34.53%] max-w-full overflow-hidden max-h-full"
        alt=""
        src="/vector12.svg"
        />
    </div>
    </div>
);
};

export default ProductCard;
