import { FunctionComponent, useState , useEffect} from "react";
import Navbar from "../components/navbar";
import Slider from "../components/slider";
import { Router, useNavigate,Link } from "react-router-dom";
import ProductTemplate from "../components/ProductTemplate";
import Products from "../productData";
import SideNavbar from "../components/sidenavbar";
import ProdTem from "../components/productTem";
import ProductCard from "../components/prodCard";


type Product = {
  id: number;
  name: string;
  imageSrc: string;
  store: string;
  price: number;
  d_price: number;
  discount: number;
  description: string;
  link: string;
};

const Desktop1: FunctionComponent = () => {

  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:3001/prod/products");
        console.log("fetched");
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();

        console.log(data);
        setProducts(data);

      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);


  const navigate = useNavigate();
  const [SelectedProduct, setSelectedProduct] = useState<Product | null>(null);

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    navigate(`/desktop-3/${product.id}`);
  };

  const [selectedOption, setSelectedOption] = useState<string>('');

  const handleSelectOption = (option: string) => {
    setSelectedOption(option);
  };

  const handleAddToCartClick = (product : Product) => {
    setSelectedProduct(product);
    navigate(`/desktop-2/${product.id}`);
  };

  return (
    <div className="bg-white w-full h-[1024px] overflow-hidden text-left text-base text-darkslategray-100 font-sora">
      <div className="absolute w-full top-[0px] flex flex-wrap -mx-2 top-[505px] left-[362px]">
      <Slider/>
      </div>
      <Navbar/>
      <div className="relative w-full h-full slider-inner-container">
      <img
        className="absolute h-[33.01%] w-[37.57%] top-[15.23%] right-[2.22%] bottom-[51.76%] left-[25.21%] max-w-full overflow-hidden max-h-full x-20"
        alt=""
        src="/ads.png"
      />
      <img
        className="absolute h-[33.01%] w-[37.57%] top-[15.23%] right-[2.22%] bottom-[51.76%] left-[65.21%] max-w-full overflow-hidden max-h-full y-20"
        alt=""
        src="/ads.png"
      />
      <img
        className="absolute h-[33.01%] w-[37.57%] top-[15.23%] right-[2.22%] bottom-[51.76%] left-[105.21%] max-w-full overflow-hidden max-h-full z-50"
        alt=""
        src="/ads.png"
      />
      </div>

      <SideNavbar onSelect={handleSelectOption} />

      <div className="absolute w-full top-[0px] flex flex-wrap -mx-2">
      {products.map((product) => (
        <ProductCard
          key={product.id}
          product={product}
          onProductClick={handleProductClick}
          onAddToCartClick={handleAddToCartClick}
        />
      ))}
      </div>



      {SelectedProduct && (
        <ProductTemplate product={SelectedProduct} />
        )}

      {Products.map((product) => (
        <Link to={`/desktop-3/${product.id}`} key={product.id}>
          <div
            className="cursor-pointer"
            onClick={() => handleProductClick(product)}
          >
          </div>
          </Link>
        ))}


          {SelectedProduct && (
              <ProdTem product={SelectedProduct} />
              )}

        {Products.map((product) => (
        <Link to={`/desktop-2/${product.id}`} key={product.id}>
          <div
            className="cursor-pointer"
            onClick={() => handleAddToCartClick(product)}
          >
          </div>
          </Link>
        ))}

      </div>

  );
};

export default Desktop1;
