import React, { useState } from 'react';

function SideNavbar({ onSelect }) {
const [selectedOption, setSelectedOption] = useState<string>('');

const handleOptionClick = (option) => {
    setSelectedOption(option);
    onSelect(option); // Call the onSelect function passed from the parent component (desktop1)
};

return (
    <div className="absolute top-[111px] left-[0px] w-[331px] h-[914px] text-7xl text-black">
    <div className={`absolute top-[0px] left-[0px] bg-whitesmoke-100 w-[331px] h-[1214px]`} />
    <div className={`absolute top-[49px] left-[0px] bg-silver w-[331px] h-[83px] ${selectedOption === 'all' ? 'selected' : ''}`} onClick={() => handleOptionClick('all')} />
    <div
        className={`absolute top-[calc(50%-_212px)] right-[20px] font-light inline-block w-[276px] h-[57px] ${selectedOption === 'Sports, Fitness' ? 'selected' : ''}`}
        onClick={() => handleOptionClick('Sports, Fitness')}
    >
        <p className="m-0"></p>
        <p className="m-0">Sports, Fitness</p>
    </div>
    {/* Add similar logic for other menu options */}
    <div
        className={`absolute top-[calc(50%_+_62px)] right-[20px] font-light inline-block w-[276px] h-[57px] ${selectedOption === 'tv' ? 'selected' : ''}`}
        onClick={() => handleOptionClick('tv')}
    >
        <p className="m-0"></p>
        <p className="m-0">TV, Appliances</p>
    </div>

    <div
        className={`absolute top-[calc(50%_-_34px)] right-[20px] font-light inline-block w-[276px] h-[57px] ${selectedOption === 'kitchen' ? 'selected' : ''}`}
        onClick={() => handleOptionClick('kitchen')}
    >
        <p className="m-0">Kitchen, Home</p>
    </div>

    <div
        className={`absolute top-[calc(50%_-_121px)] right-[20px] font-light inline-block w-[276px] h-[57px] ${selectedOption === 'electronics' ? 'selected' : ''}`}
        onClick={() => handleOptionClick('electronics')}
    >
        <p className="m-0"></p>
        <p className="m-0">Electronics</p>
    </div>

    <div
        className={`absolute top-[calc(50%_-_295px)] right-[20px] font-light inline-block w-[276px] h-[57px] ${selectedOption === 'mobile' ? 'selected' : ''}`}
        onClick={() => handleOptionClick('mobile')}
    >
        Mobiles, Computers
    </div>

    <div className="absolute top-[calc(50%_-_382px)] right-[72px] font-light inline-block w-56 h-[57px]">
        Todayâ€™s offers
    </div>
    <div className="absolute top-[49px] left-[0px] bg-darkslategray-100 w-4 h-[83px]" />
    </div>
);
}

export default SideNavbar;
