import {React , useState} from "react";
import Products from "../productData";
import { Router, useNavigate,Link } from "react-router-dom";



const ProdTem = ({ product }) => {
        const navigate = useNavigate();

        const handleProductClick = () => {
        navigate(`/desktop-3/${product.id}`); // Redirect to /desktop-3 with the product ID
        };

return(
        <div className="grid col-span-1">
                <div className="grid grid-cols-2 px-4 py-8 bg-whitesmoke-100 m-4">
                        <div className="m-2 grid span-col-1 bg-white">
                                <div className="flex items-center justify-center">
                                <img
                                        src={product.imageSrc}
                                        alt={product.name}
                                        onClick={() => handleProductClick}
                                />
                                </div>
                                <h2 className="px-2 text-md font-bold">{product.store}</h2>
                        </div>
        <div className="m-2 flex flex-col justify-between w-3/4">
                <div>
                        <h2 className="inline-block text-md">{product.name}</h2>
                        <div className="flex items-center space-x-2">
                        <span>₹{product.price}</span>
                        <span>₹{product.d_price}</span>
                        </div>
                </div>
                <div className="flex items-center justify-between mt-4 space-x-2">
                        <a style={{textDecoration:"none"}} href={product.link} className="flex items-center justify-center space-x-2 text-white px-4 py-2 rounded">
                        <img src="/vector11.svg" alt="Shop now" className="w-4 h-4" />
                        <span className="text-darkslategray-100">Shop now</span>
                        </a>
                        <button className="flex items-center justify-center space-x-2 bg-red-500 text-white px-4 py-2 rounded">
                        <span>Delete</span>
                        </button>
                </div>
                <div className="flex items-center space-x-2 mt-2">
                        <img src="/telegram-icon.svg" alt="Telegram" className="w-6 h-6" />
                        <img src="/whatsapp-icon.svg" alt="WhatsApp" className="w-6 h-6" />
                        <img src="/pixelarticonscopy.svg" alt="Copy" className="w-6 h-6" />
                </div>
                </div>
                </div>
        </div>
        );
};
export default ProdTem;
