import {React , useState} from "react";
import Products from "../productData";
import { Router, useNavigate,Link } from "react-router-dom";

    type Product = {
        id: number;
        name: string;
        imageSrc: string;
        store: String,
        price: number,
        description: String
    };




const prodTem = ({product}) =>{

    const navigate = useNavigate();
    const [SelectedProduct, setSelectedProduct] = useState<Product | null>(null); // Specify the type for useState

    const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    navigate(`/desktop-3/${product.id}`);// Redirect to /desktop-3
    };

    const [selectedOption, setSelectedOption] = useState<string>(''); // Specify the type as string

    const handleSelectOption = (option: string) => {
    setSelectedOption(option);
    };

    return(

            <div className="absolute top-[293px] left-[90px] w-[630px] h-[205px]">
            <div className="absolute top-[0px] left-[0px] bg-whitesmoke-100 w-[630px] h-[205px]" />
            <div className="absolute top-[16.4px] left-[17.93px] bg-white w-[264.19px] h-[167.51px]" />
            <img
            className="absolute top-[22.26px] left-[75.31px] w-[151.82px] h-[155.8px] object-cover"
            alt=""
            src={product.imageSrc}
            onClick={() => handleProductClick(Products.id)}
            />
            <div className="absolute top-[26.94px] left-[317.99px] inline-block w-[237.89px] h-[45.69px]">
            {product.name}
            </div>
            <div className="absolute top-[86.69px] left-[317.99px] inline-block w-[86.07px] h-[26.94px]">
            â‚¹{product.price}
            </div>
            <div className="absolute top-[160.49px] left-[29.89px] font-semibold inline-block w-[82.49px] h-[11.71px]">
            {product.store}
            </div>
            <div className="absolute top-[152.29px] left-[327.55px] inline-block w-[105.2px] h-[28.11px]">{`Shop now `}</div>
            <div className="absolute top-[152.29px] left-[449.49px] inline-block w-[69.34px] h-[28.11px]">
            Delete
            </div>
            <div className="absolute top-[143.59px] left-[438.23px] box-border w-px h-[37.31px] border-r-[1px] border-solid border-silver" />
            <img
            className="absolute top-[143.5px] left-[529.68px] w-px h-[36.9px]"
            alt=""
            src="/line-81.svg"
            />
            <img
            className="absolute h-[13.71%] w-[4.55%] top-[48.57%] right-[7.97%] bottom-[37.71%] left-[87.48%] max-w-full overflow-hidden max-h-full"
            alt=""
            src="/twitter-icon.svg"
            />
            <img
            className="absolute h-[57.14%] w-[41.56%] top-[30.86%] right-[7.97%] bottom-[12%] left-[50.47%] max-w-full overflow-hidden max-h-full"
            alt=""
            src="/whatsapp-icon1.svg"
            />
            <img
            className="absolute top-[26.94px] left-[551.1px] w-[28.69px] h-[28.11px] overflow-hidden"
            alt=""
            src="/pixelarticonscopy1.svg"
            />

        {SelectedProduct && (
            <prodTem product={SelectedProduct} /> // Render the ProductTemplate
            )}

        {Products.map((product) => (
            <Link to={`/desktop-3/${product.id}`} key={product.id}>
            <div
                className="cursor-pointer"
                onClick={() => handleProductClick(product)}
            >
            </div>
            </Link>
            ))}
            </div>
        );
};

export default prodTem;
