import React, { useEffect, useState, useRef } from 'react';

const Slider = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const sliderRef = useRef(null);

    const moveSlider = () => {
        setCurrentSlide((prevSlide) => {
            const totalSlides = sliderRef.current.children.length;
            if (prevSlide >= totalSlides - 1) {
                return 0; // reset to the first slide
            }
            return prevSlide + 1;
        });
    };

    useEffect(() => {
        const interval = setInterval(moveSlider, 3000);
        return () => clearInterval(interval);
    }, []);

    useEffect(() => {
        const slider = sliderRef.current;
        slider.style.transition = 'transform 0.5s ease-in-out';
        slider.style.transform = `translateX(-${currentSlide * 100}%)`;

        // handle cloning for continuous effect
        if (currentSlide === slider.children.length - 1) {
            setTimeout(() => {
                slider.style.transition = 'none';
                slider.style.transform = 'translateX(0)';
                setCurrentSlide(0);
            }, 500);
        }
    }, [currentSlide]);

    return (
        <div className="relative overflow-hidden w-full h-[300px] bg-whitesmoke-100">
            <div ref={sliderRef} className="flex">
                {[
                    '/kithremovebgpreview-1@2x.png',
                    '/mobilesremovebgpreview-1@2x.png',
                    '/earphonesremovebgpreview-1@2x.png',
                    '/lapremovebgpreview-1@2x.png',
                    '/lapremovebgpreview-1@2x.png',
                    '/lapremovebgpreview-1@2x.png',
                    '/lapremovebgpreview-1@2x.png',
                ].map((src, index) => (
                    <div
                        key={index}
                        className="m-4 rounded-full bg-darkslategray-100 h-[200px] w-[200px] flex-shrink-0 flex justify-center items-center"
                    >
                        <img
                            className="w-[150px] h-[150px] object-contain"
                            alt=""
                            src={src}
                        />
                    </div>
                ))}
                {[
                    '/kithremovebgpreview-1@2x.png',
                    '/mobilesremovebgpreview-1@2x.png',
                    '/earphonesremovebgpreview-1@2x.png',
                ].map((src, index) => (
                    <div
                        key={`clone-${index}`}
                        className="m-4 rounded-full bg-darkslategray-100 h-[200px] w-[200px] flex-shrink-0 flex justify-center items-center"
                    >
                        <img
                            className="w-[150px] h-[150px] object-contain"
                            alt=""
                            src={src}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Slider;
