import React, { useEffect, useState } from 'react';

const Slider = () => {
const [currentSlide, setCurrentSlide] = useState(0);

const sliderContainers = document.querySelectorAll('.slider-container');
const slideWidth = sliderContainers[0]?.offsetWidth || 0;

const moveSlider = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % sliderContainers.length);
};

useEffect(() => {
    const interval = setInterval(moveSlider, 3000);
    return () => clearInterval(interval);
}, []);

return (
    <div className="grid grid-cols-2 sm:grid-cols-5 bg-whitesmoke-100 w-100 h-[270px] px-4 py-4" style={{zIndex:0}}>
    <div className="">
        <div className="">
        <div className="rounded-[50%] bg-darkslategray-100 h-[200px] px-16 py-8">
            <img
            className="w-[250px] h-[250px] py-2"
            alt=""
            src="/kithremovebgpreview-1@2x.png"
            />
        </div>
        </div>
    </div>

    <div className="">
    <div className="rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px]">

    <img
    className="w-[191.04px] h-[172px] object-cover"
    alt=""
    src="/mobilesremovebgpreview-1@2x.png"
    />
    </div>
    </div>
    <div className="">
    <div className="rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px]">

    <img
    className="w-[202.67px] h-[133px] object-cover"
    alt=""
    src="/earphonesremovebgpreview-1@2x.png"
    />
    </div>
    </div>
    <div className="">
    <div className="rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px]">
    <img
    className="w-[171.04px] h-[172px] object-cover"
    alt=""
    src="/lapremovebgpreview-1@2x.png"
    />
    </div>
    </div>

    <div className="">
    <div className="rounded-[50%] bg-darkslategray-100 w-[206.63px] h-[162px]">
    <img
    className=" w-[171.04px] h-[172px]  object-cover"
    alt=""
    src="/lapremovebgpreview-1@2x.png"
    />
    </div>
    </div>
    </div>

    );
};

export default Slider;
