import React from "react";

type Product = {
id: number;
name: string;
imageSrc: string;
store: string;
price: number;
d_price: number;
discount: number;
description: string;
link: string;
};

type ProductCardProps = {
product: Product;
onProductClick: (product: Product) => void;
onAddToCartClick: (product: Product) => void;
};

const ProductCard: React.FC<ProductCardProps> = ({
product,
onProductClick,
onAddToCartClick,
}) => {

return (
    <div className="flex flex-col items-center m-4">
    <div className="relative w-64 h-80 top-[713px] left-[340px]">
        <div className="absolute inset-0 bg-whitesmoke-100 rounded-md" />
        <div className="absolute top-[0px] left-[0px] bg-white shadow-[1px_1px_2px_rgba(0,_0,_0,_0.25)_inset] w-full h-[70%]">
        <img
            className="absolute inset-0 object-cover cursor-pointer top-[25%] h-[60%] left-[25%]"
            src={product.imageSrc}
            alt={product.name}
            onClick={() => onProductClick(product)}
            />
            </div>
        <div className="absolute flex flex-col justify-between p-4">
        <div>
            <div className="text-red">{product.discount}% off</div>
            <div className="text-black font-bold">₹{product.d_price}</div>
            <div className="text-gray-500 line-through">₹{product.price}</div>
        </div>
        <div className="absolute flex justify-between top-[72%]">
            <a
            className="text-blue-500 underline"
            href={product.link}
            target="_blank"
            rel="noopener noreferrer"

            style={{
                textDecoration:"none",
                    color: "inherit",
                    cursor: "pointer",
                }}>
            {product.name}
            </a>
        </div>
        <div className="items-center h-6">
            <button
            className="px-2 py-2 text-white rounded-md w-[50%] text-left text-base bg-darkslategray-100 font-sora h-8"
            onClick={() => onAddToCartClick(product)}
            >
            Add to Cart
            </button>
            </div>
        </div>
    </div>
    </div>
);
};

export default ProductCard;
