const Slider = () => {
const [currentSlide, setCurrentSlide] = useState(0);

const sliderContainers = document.querySelectorAll('.slider-container');
const slideWidth = sliderContainers[0]?.offsetWidth || 0;

const moveSlider = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % sliderContainers.length);
};

useEffect(() => {
    const interval = setInterval(moveSlider, 3000);
    return () => clearInterval(interval);
}, []);

return (
    <div className="m-4 grid grid-cols-2 sm:grid-cols-5 bg-whitesmoke-100 w-100 h-[300px] px-4 py-2" style={{zIndex:0}}>
            <div className="rounded-[50%] bg-darkslategray-100 h-[200px] px-16 py-8">
                <img
                className="w-[250px] h-[250px] object-contain"
                alt=""
                src="/kithremovebgpreview-1@2x.png"
                />
            </div>

    <div className="m-4">
    <div className="rounded-[50%] bg-darkslategray-100 h-[200px] px-16 py-8">
            <img
            className="w-[250px] h-[250px] object-contain"
            alt=""
            src="/mobilesremovebgpreview-1@2x.png"
            />
    </div>
    </div>
