    <img
        className="w-[250px] h-[250px] object-contain"
        alt=""
        src="/earphonesremovebgpreview-1@2x.png"
    />
    </div>
    </div>
    <div className="m-4">
    <div className="rounded-[50%] bg-darkslategray-100 h-[200px] px-16 py-8">
    <img
    className="w-[250px] h-[250px] object-contain"
    alt=""
    src="/lapremovebgpreview-1@2x.png"
    />
    </div>
    </div>

    <div className="m-4">
    <div className="rounded-[50%] bg-darkslategray-100 h-[200px] px-16 py-8">
    <img
    className="w-[250px] h-[250px] object-contain"
    alt=""
    src="/lapremovebgpreview-1@2x.png"
    />
    </div>
    </div>
    </div>

    );
};

export default Slider;
