import React, { useState, useRef } from 'react';
import './css/voiceInterface.css';

const VoiceInterface = () => {
    const [status, setStatus] = useState("Click the button to start");
    const [isListening, setIsListening] = useState(false);
    const recognitionRef = useRef(null);

    // Initialize Speech Recognition
    const initializeRecognition = () => {
        const recognition = new window.webkitSpeechRecognition();
        recognition.continuous = true;
        recognition.interimResults = false;

        recognition.onstart = () => {
            setStatus("Listening...");
        };

        recognition.onresult = (event) => {
            const transcript = event.results[event.results.length - 1][0].transcript.trim();
            setStatus(`You said: ${transcript}`);
            processUserInput(transcript);
        };

        recognition.onerror = (event) => {
            setStatus(`Error occurred: ${event.error}`);
        };

        recognition.onend = () => {
            setStatus("Stopped listening.");
        };

        recognitionRef.current = recognition;
    };

    const startListening = () => {
        if (!recognitionRef.current) {
            initializeRecognition();
        }
        recognitionRef.current.start();
        setIsListening(true);
    };

    const stopListening = () => {
        if (recognitionRef.current) {
            recognitionRef.current.stop();
        }
        setIsListening(false);
    };

    const pauseListening = () => {
        if (recognitionRef.current) {
            recognitionRef.current.stop();
        }
        setIsListening(false);
        setStatus("Paused listening.");
    };

    const processUserInput = (transcript) => {
        fetch('/processVoiceInput', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ userInput: transcript })
        })
        .then(response => response.json())
        .then(data => {
            const utterance = new SpeechSynthesisUtterance(data.response);
            speechSynthesis.speak(utterance);
        })
        .catch(error => {
            console.error('Error processing input:', error);
        });
    };

    return (
    <div className="voice-interface">
        <button className="cursor-pointer voice-button" onClick={startListening} disabled={isListening}>Start</button>
        <button className="cursor-pointer voice-button" onClick={stopListening} disabled={!isListening}>Stop</button>
        <button className="voice-button" onClick={pauseListening} disabled={!isListening}>Pause</button>
        <div id="status" className="status">{status}</div>
    </div>
    );
};

export default VoiceInterface;
