import { FunctionComponent, useState, useEffect} from "react";
import Navbar from "../components/navbar";
import ProductTemplate from "../components/ProductTemplate";
import { useParams } from "react-router-dom";
import '../ProductTemplate.css'
import SideNavbar from "../components/sidenavbar";

type Desktop3Props = {};

type Product = {
  id: number;
  name: string;
  imageSrc: string;
  store: string;
  price: number;
  d_price: number;
  discount: number;
  description: string;
  link: string;
};


const Desktop3: FunctionComponent<Desktop3Props> = () => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(false);
  const [selectedOption,setSelectedOption] = useState<string | null>(null);

    const handleSelectOption = (selectedOption: string | null) => {
      setSelectedOption(selectedOption);
    };

    const toggleSidebar = () => {
      setIsSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setIsSidebarVisible(false);
  };

    const [Cartproducts, setProducts] =  useState<Product | null>(null);

    useEffect(() => {
        const getCartProducts = async () => {
            try {
                const response = await fetch('http://localhost:3001/cart/get-products');
                if (response.ok) {
                    const Cartproducts = await response.json();
                    setProducts(Cartproducts);
                } else {
                    console.error('Failed to fetch products');
                }
            } catch (error) {
                console.error('Error occurred', error);
            }
        };
        getCartProducts();
    }, []);


  if (!Cartproducts) {
    // Handle the case where no matching product is found
    return <div>Product not found</div>;
  }

  return (
    <div className="relative bg-white w-full h-[1024px] overflow-hidden text-left text-3xl text-darkslategray-100 font-sora">
      <Navbar toggleSidebar={toggleSidebar}/>
        {/* form div */}
        {isSidebarVisible && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={closeSidebar}
        />
      )}
      <SideNavbar onSelect={handleSelectOption} isVisible={isSidebarVisible} />

      <div className="flex items-center justify-center">
        {Cartproducts && <ProductTemplate product={Cartproducts} />}
      </div>
    </div>

  );
};

export default Desktop3;
