import { FunctionComponent, useState , useEffect} from "react";
import Navbar from "../components/navbar";
import Slider from "../components/slider";
import { Router, useNavigate,Link } from "react-router-dom";
import ProductTemplate from "../components/ProductTemplate";
import Products from "../productData";
import SideNavbar from "../components/sidenavbar";
import ProdTem from "../components/productTem";
import ProductCard from "../components/prodCard";
import VoiceInterface from "../components/voiceInterface";


type Product = {
  id: number;
  name: string;
  imageSrc: string;
  store: string;
  price: number;
  d_price: number;
  discount: number;
  description: string;
  link: string;
};

const Desktop1: FunctionComponent = () => {

  const [products, setProducts] = useState<Product[]>([]);
  const [isSidebarVisible, setIsSidebarVisible] = useState(false);
  const [userId, setUserId] = useState<any>();

  const getCred = async() => {
    fetch('http://localhost:3001/auth-check',
      {
        method:"GET",
        credentials: 'include',
      }).then(response => {
        if(response.ok){
          const UserIdd = response.json();
          setUserId(UserIdd);
        }
        else{
          console.log("Some error", response);
        }
    })
  };

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:3001/prod/products");
        console.log("fetched");
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();

        console.log(data);
        setProducts(data);

      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);


  const navigate = useNavigate();
  const [SelectedProduct, setSelectedProduct] = useState<Product | null>(null);

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    navigate(`/desktop-3/${product.id}`);
  };

  const [selectedOption, setSelectedOption] = useState<string>('');

  const handleSelectOption = (option: string | null) => {
    setSelectedOption(option as any);
  };

  const handleAddToCartClick = (product : Product) => {
    setSelectedProduct(product);
    getCred();
    fetch('http://localhost:3001/cart/add-to-cart',{
      method: "POST",
      headers:{
        "Content-type" : "application/json"
      },
      credentials: 'include',
      body: JSON.stringify({products : product})

    }).then((response) =>{
      if(response.ok){
        console.log("Added Successfully");
      }
    });
    navigate(`/desktop-2/${product.id}`);
  };

  const toggleSidebar = () => {
    setIsSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setIsSidebarVisible(false);
  };


  return (
    <div className="bg-white w-full text-left text-base text-darkslategray-100 font-sora">
      <Navbar toggleSidebar={toggleSidebar}/>
      {isSidebarVisible && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={closeSidebar}
        />
      )}
      <div className="relative">
          <SideNavbar onSelect={handleSelectOption} isVisible={isSidebarVisible} />
          <div className="py-10" style={{ overflow: 'hidden', width: '100%', height: '700px' }}>
            <div className="slider-inner-container">
              <div className="slider-container">
                <img
                  className="m-4"
                  alt="ad 1"
                  src="/ads.png"
                />
              </div>
              <div className="slider-container">
                <img
                  className="m-4"
                  alt="ad 2"
                  src="/ads.png"
                />
              </div>
              <div className="slider-container">
                <img
                  className="m-4"
                  alt="ad 3"
                  src="/ads.png"
                />
              </div>
              <div className="slider-container">
                <img
                  className="m-4"
                  alt="ad 4"
                  src="/ads.png"
                />
              </div>
          </div>
          </div>
          <div className="px-4 py-2">
            <Slider />
          </div>
      </div>


      <div className="grid grid-cols-2 md:grid-cols-4 xl:grid-cols-5">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onProductClick={handleProductClick}
            onAddToCartClick={handleAddToCartClick}
          />
        ))}
      </div>

      <VoiceInterface onSelect={handleVoiceOption} isVisible={isVisible} />

      {SelectedProduct && (
        <ProductTemplate product={SelectedProduct} />
        )}

      {Products.map((product) => (
        <Link to={`/desktop-3/${product.id}`} key={product.id}>
          <div
            className="cursor-pointer"
            onClick={() => handleProductClick(product)}
          >
          </div>
          </Link>
        ))}


          {SelectedProduct && (
              <ProdTem product={SelectedProduct} />
              )}

        {Products.map((product) => (
        <Link to={`/desktop-2/${product.id}`} key={product.id}>
          <div
            className="cursor-pointer"
            onClick={() => handleAddToCartClick(product)}
          >
          </div>
          </Link>
        ))}

      </div>



  );
};

export default Desktop1;
