import React from "react";

type Product = {
  id: number;
  name: string;
  imageSrc: string;
  store: string;
  price: number;
  d_price: number;
  discount: number;
  description: string;
  link: string;
};

type ProductCardProps = {
  product: Product;
  onProductClick: (product: Product) => void;
  onAddToCartClick: (product: Product) => void;
};

const ProductCard: React.FC<ProductCardProps> = ({
  product,
  onProductClick,
  onAddToCartClick,
}) => {
  return (
    <div className="flex flex-col items-center m-4">
      <div className="relative w-64 h-80">
        <div className="absolute inset-0 bg-whitesmoke-100 rounded-md" />
        <img
          className="w-full h-3/5 object-cover cursor-pointer"
          src={product.imageSrc}
          alt={product.name}
          onClick={() => onProductClick(product)}
        />
        <div className="absolute inset-0 flex flex-col justify-between p-4">
          <div>
            <div className="text-red">{product.discount}% off</div>
            <div className="text-black font-bold">₹{product.d_price}</div>
            <div className="text-gray-500 line-through">₹{product.price}</div>
          </div>
          <div className="flex justify-between items-center">
            <a
              className="text-blue-500 underline"
              href={product.link}
              target="_blank"
              rel="noopener noreferrer"
            >
              {product.name}
            </a>
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded-md"
              onClick={() => onAddToCartClick(product)}
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
