import React from "react";

type Product = {
id: number;
name: string;
imageSrc: string;
store: string;
price: number;
d_price: number;
discount: number;
description: string;
link: string;
};

type ProductCardProps = {
product: Product;
onProductClick: (product: Product) => void;
onAddToCartClick: (product: Product) => void;
};

const ProductCard: React.FC<ProductCardProps> = ({
product,
onProductClick,
onAddToCartClick,
}) => {
return (
    <div className="flex flex-col items-center p-4 m-4 border border-gray-300 rounded-md">
    <img
        className="w-40 h-40 object-cover cursor-pointer"
        src={product.imageSrc}
        alt={product.name}
        onClick={() => onProductClick(product)}
    />
    <div className="mt-4 text-center">
        <a
        className="text-blue-500 underline"
        href={product.link}
        target="_blank"
        rel="noopener noreferrer"
        >
        {product.name}
        </a>
    </div>
    <div className="mt-2 text-red">{product.discount}% off</div>
    <div className="mt-2 text-black">₹{product.d_price}</div>
    <div className="mt-2 text-gray-500">₹{product.price}</div>
    <button
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md"
        onClick={() => onAddToCartClick(product)}
    >
        Add to Cart
    </button>
    </div>
);
};

export default ProductCard;
