import { FunctionComponent, useState } from "react";
import Navbar from "../components/navbar";
import { useParams } from "react-router-dom";
import Products from "../productData";
import ProdTem from "../components/productTem";
import SideNavbar from "../components/sidenavbar";


type Product = {
  id: number;
  name: string;
  imageSrc: string;
  store: string;
  price: number;
  d_price: number;
  discount: number;
  description: string;
  link: string;
};

type Desktop2Props = {};

const Desktop2: FunctionComponent<Desktop2Props> = () => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(false);
  const [selectedOption,setSelectedOption] = useState<string | null>(null);

  useEffect(() => {
    const getCartProducts = async () => {
      try {
        const response = await fetch('http://localhost:3001/cart/get-products');
        if (response.ok) {
          const data = await response.json();
          setCartProducts(data.products);
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error occurred', error);
      }
    };
    getCartProducts();
  }, []);

  if (!cartProducts.length) {
    // Handle the case where no products are found
    return <div>No products found</div>;
  }


  const { productId } = useParams();

  const handleSelectOption = (selectedOption: string | null) => {
    setSelectedOption(selectedOption);
  };

    const toggleSidebar = () => {
      setIsSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setIsSidebarVisible(false);
  };


    // Assuming you have an array of products, you can filter the product with the matching ID
    const selectedProduct = Products.find(product => product.id.toString() === productId);

    if (!selectedProduct) {
      // Handle the case where no matching product is found
      return <div>Product not found</div>;
    }
  return (
    <div className="relative bg-white w-full h-[1024px] overflow-hidden text-left text-base text-darkslategray-100 font-sora">
        <Navbar toggleSidebar={toggleSidebar}/>
        {/* form div */}
        {isSidebarVisible && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={closeSidebar}
        />
      )}
      <SideNavbar onSelect={handleSelectOption} isVisible={isSidebarVisible} />
      <b className="absolute top-[195px] left-[89px] text-[45px] inline-block text-black w-[549px] h-[33px]">
        Shopping cart
      </b>
      <div className="absolute top-[257.98px] left-[88.48px] box-border w-[1297.03px] h-px border-t-[1px] border-solid border-black" />
        <ProdTem product={selectedProduct} />
      </div>

    );
  };

export default Desktop2;
