import React, { useState } from 'react';

type SideNavbarProps = {
    onSelect: (option: string) => void;
    isVisible: boolean;
};

const SideNavbar: React.FC<SideNavbarProps> = ({ onSelect, isVisible }) => {
    const [activeIndex, setActiveIndex] = useState(0);

    const menuItems = [
        'Home, Kitchen',
        'Electronics',
        'Mobiles, Computers',
        'TV, Appliances',
        'Sports, Fitness'
    ];

    return (
        <div className={`fixed left-0 h-full bg-whitesmoke-100 z-50 transition-transform transform ${isVisible ? 'translate-x-0' : '-translate-x-full'}`} style={{ width: '300px' }}>
            <div className='py-8'>
                <ul className="list-none p-0 m-0">
                    {menuItems.map((item, index) => (
                        <li
                            key={index}
                            className="relative px-4 py-8 cursor-pointer"
                            onClick={() => {
                                setActiveIndex(index);
                                onSelect(item);
                            }}
                        >
                            {activeIndex === index && (
                                <div className='grid grid-cols-12 absolute inset-0 bg-silver z-0'>
                                    <div className='grid col-span-1 bg-darkslategray-100' style={{ zIndex: 1 }}></div>
                                </div>
                            )}
                            <a className="px-8 relative z-10 text-lg">{item}</a>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default SideNavbar;
